import RecordPresenter from "../Presenter/RecordPresenter"
import { OuterBox, Text } from "../Util/BaseStyles"
import InstrumentToggle from "./InstrumentToggle";
import GenreDropdown from "./GenreDropdown"

interface Props{
    instruments: string[];
    onToggleInstrument: (instrument: string) => void;
    onGenreChange: (genre: string) => void;
    onFileChange: (file: File | null) => void;
    audioFile: File | null;
    genre: string;
}

export default function CreateSongView(props:Props){
    const {instruments, onToggleInstrument, onGenreChange, onFileChange, audioFile, genre} = props;
    const prompt = audioFile ? `A ${genre}-themed song with the following instruments ${instruments.join(', ')}` : '';

    return (
    <OuterBox>
        <Text>CreateSongView {prompt}</Text>
        <InstrumentToggle instruments={instruments} onToggleInstrument={onToggleInstrument} />
        <GenreDropdown onChange={onGenreChange} />
        <RecordPresenter onFileChange={onFileChange} />
    </OuterBox>)
}
