// InstrumentToggle.tsx
import { Button } from '../Util/BaseStyles';

interface Props {
    instruments: string[];
    onToggleInstrument: (instrument: string) => void;
}

const availableInstruments = ['Guitar', 'Piano', 'Flute']; // Add more as required

export default function InstrumentToggle({ instruments, onToggleInstrument }: Props) {
    return (
        <div>
            {availableInstruments.map(instrument => (
                <Button 
                    key={instrument} 
                    onClick={() => onToggleInstrument(instrument)}
                    style={{ backgroundColor: instruments.includes(instrument) ? 'CornflowerBlue' : 'white' }}
                >
                    {instrument}
                </Button>
            ))}
        </div>
    );
}
